CREATE TABLE dbo.LOCATIONS (LOCATION_ID int not null, STREET_ADDRESS  nVARCHAR(100) null, POSTAL_CODE nVARCHAR(100) null, CITY nVARCHAR(100) null, STATE_PROVINCE nVARCHAR(100) null, COUNTRY_ID nVARCHAR(100) null) WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.JOBS (JOB_ID nVARCHAR(100) not null, JOB_TITLE nVARCHAR(100) not null, MIN_SALARY int null, MAX_SALARY int null) WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.EMPLOYEES (EMPLOYEE_ID int not null, FIRST_NAME nVARCHAR(100), LAST_NAME nVARCHAR(100) null, EMAIL nVARCHAR(100) null, PHONE_NUMBER nVARCHAR(100) null, HIRE_DATE DATEtime null, JOB_ID nVARCHAR(100) null, SALARY float null, COMMISSION_PCT float null, MANAGER_ID int null, DEPARTMENT_ID int null) WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.DEPARTMENTS (DEPARTMENT_ID int not null, DEPARTMENT_NAME nVARCHAR(100) null, MANAGER_ID int null, LOCATION_ID int null) WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.COUNTRIES (COUNTRY_ID nVARCHAR(10) not null, COUNTRY_NAME nVARCHAR(100) null, REGION_ID int null) WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.PROMOTIONS (PROMO_ID int not null, PROMO_NAME nVARCHAR(100)) WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.PRODUCT_DESCRIPTIONS (PRODUCT_ID INT NOT NULL, LANGUAGE_ID nVARCHAR(3) NOT NULL, TRANSLATED_NAME nVARCHAR(2000) NOT NULL, TRANSLATED_DESCRIPTION nVARCHAR(2000)) WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.PRODUCT_INFORMATION (PRODUCT_ID INT NOT NULL, PRODUCT_NAME nVARCHAR(50), CATEGORY_ID INT, WEIGHT_CLASS INT, WARRANTY_PERIOD nVARCHAR(50), SUPPLIER_ID INT, PRODUCT_STATUS nVARCHAR(50), LIST_PRICE INT, MIN_PRICE INT,CATALOG_URL nVARCHAR(50))  WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.ORDERS (ORDER_ID INT not null, ORDER_DATE datetime not null, ORDER_MODE nVARCHAR(8), CUSTOMER_ID int not null, ORDER_STATUS int, ORDER_TOTAL Numeric, SALES_REP_ID int, PROMOTION_ID int)  WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.ORDER_ITEMS (ORDER_ID int not null, LINE_ITEM_ID int not null, PRODUCT_ID int not null, UNIT_PRICE Numeric, QUANTITY int)  WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
CREATE TABLE dbo.INVENTORIES (PRODUCT_ID int not null, WAREHOUSE_ID int not null, QUANTITY_ON_HAND int not null)  WITH(CLUSTERED COLUMNSTORE INDEX, DISTRIBUTION = ROUND_ROBIN)
